import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Navbar from './NavBar'
import Footer from './Footer'
import Select from 'react-select'


function SearchFlight() {
    const [orgins, setorgins] = useState([])
    const [destinations, setdestinations] = useState([])

    useEffect(()=>{
        depatureandArrivalOptions()
    },[])

    async function depatureandArrivalOptions(){
       const res=await axios.get('http://localhost:8080/flight/viewFlight')
       const data=res.data

       const orginAirportOptions=data.map(flight=>(
           {
            "value":flight.sourceAirport,
            "label":flight.sourceAirport
           }
       ))

       setorgins(orginAirportOptions)

       const destinationAirportOptions=data.map(flight=>(
        {
         "value":flight.destinationAirport,
         "label":flight.destinationAirport
        }
    ))

        setdestinations(destinationAirportOptions)

        console.log(orginAirportOptions)
        console.log("---------------------------------")
        console.log(destinationAirportOptions)

    }

    return (
        <div >
            <Navbar/>
            <div style={{color:"black"}} className="container">
                <Select options={orgins} placeholder="From"/>
                <Select options={destinations} placeholder="To"/>

            </div>

            <Footer/>
            
        </div>
    )
}

export default SearchFlight
