import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Card } from 'react-bootstrap'

function ViewFlights() {

    const [flights, setflights] = useState([])




    useEffect(() => {
        const url = 'http://localhost:8080/flight/viewFlight'
        axios.get(url).then((response) => {
            setflights(response.data)
            console.log(response.data)
        })

    }, [])


    const deleteFlight = (e) => {

        let flightId = e.target.value
        console.log(flightId)
        let url = `http://localhost:8080/flight/deleteFlight/${flightId}`
        axios.delete(url).then((res) => {
            console.log(res.data)
        })
        window.location.reload(false);
    }




    return (
        <div style={{ color: "black" }}>

            <h1>Flights Scheduled</h1>


            {
                flights.map((flight) => {
                    return (
                        <div key={flight.flightId} className="container">
                            <Card>
                                <Card.Header>{flight.flightName}</Card.Header>
                                <Card.Body>
                                    <Card.Text>
                                        {flight.flightId}<br />

                                        {flight.sourceAirport}  to  {flight.destinationAirport}<br />
                                        {flight.date}<br />
                                        {flight.departureHour}:{flight.departureMinute}<br />
                                        {flight.arrivalHour}:{flight.arrivalMinute}<br />
                                        {flight.price}<br />
                                    </Card.Text>
                                    <button variant="danger" value={flight.flightId} onClick={deleteFlight}>Delete Flight</button>
                                </Card.Body>

                            </Card>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default ViewFlights
