import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Card } from 'react-bootstrap'

function ViewFlights() {

    const [flights, setflights] = useState([])




    useEffect(() => {
        const url = 'http://localhost:8080/flight/viewFlight'
        axios.get(url).then((response) => {
            setflights(response.data)
            console.log(flights)
        })

    }, [])


    const deleteFlight = (flightId) => {
        let url = `http://localhost:8080/flight/deleteFlight/${flightId}`
        axios.delete(url).then((res) => {
            console.log()
        })
    }




    return (
        <div>

            <h1>Flights Scheduled</h1>


            {
                flights.map((flight) => {
                    return (

                        <card>
                            <Card.Header>{flight.flightName}</Card.Header>
                            <Card.Body>
                            <Card.Text>
                            {flight.flightId}
                            {flight.sourceAirport}{flight.destinationAirport}
                            {flight.Date}
                            {flight.depatureHour}:{flight.depatureMinutes}
                            {flight.arrivalHour}:{flight.arrivalMinute}
                            {flight.price}<br />
                            </Card.Text>
                            <button variant="danger" onClick={deleteFlight(flight.flightId)}>Delete Flight</button>
                            </Card.Body>
                            
                        </card>
                    )
                })
            }
        </div>
    )
}

export default ViewFlights
