import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Navbar from './NavBar'
import Footer from './Footer'
import Select from 'react-select'


function SearchFlight() {
    const [orgins, setorgins] = useState([])
    const [destinations, setdestinations] = useState([])
    const [orgin, setorgin] = useState('')
    const [destination, setdestination] = useState('')
    const [result, setresult] = useState([])

    useEffect(()=>{
        depatureandArrivalOptions()
    },[])

    async function depatureandArrivalOptions(){
       const res=await axios.get('http://localhost:8080/flight/viewFlight')
       const data=res.data

       const orginAirportOptions=data.map(flight=>(
           {
            "value":flight.sourceAirport,
            "label":flight.sourceAirport
           }
       ))

       setorgins(orginAirportOptions)

       const destinationAirportOptions=data.map(flight=>(
        {
         "value":flight.destinationAirport,
         "label":flight.destinationAirport
        }
    ))

        setdestinations(destinationAirportOptions)


    }

    const handleSearch=()=>{
        const url=`http://localhost:8080/flight/searchFlightByDestinationandSource/${destination}/${orgin}`
        axios.get(url).then((response)=>{
            setresult(response.data)
        })

    }

    return (
        <div >
            <Navbar/>
            <div style={{color:"black"}} className="container row">
                <div className="col"><Select options={orgins} placeholder="From" onChange={(e)=>{setorgin(e.value)}}/></div>
                <div className="col"><Select options={destinations} placeholder="To" onChange={(e)=>{setdestination(e.value)}}/></div>
                <div className="col"><button style={{backgroundColor:"lightblue",width:"350px"}} onClick={handleSearch}>Search</button></div>
                
                {/* {
                    result.map((flight)=>{
                        return(
                            <h1>{flight.flightName}</h1>
                        )
                    })
                } */}

<div class="container card">
   <img
    src="https://images.unsplash.com/photo-1549589237-9e70b6be4da8?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=925&q=80"
    alt="Pancake"
   />
   <div class="container__text">
    <h1>Caramel Cake Pancakes</h1>
    <div class="container__text__star">
     <span class="fa fa-star checked"></span>
     <span class="fa fa-star checked"></span>
     <span class="fa fa-star checked"></span>
     <span class="fa fa-star checked"></span>
     <span class="fa fa-star checked"></span>
    </div>
    <p>
     If you're fan of caramel cake, then you'll love our Caramel Cake Pancakes.
     We Complete these over-the-top pancakes with Caramel Syrup.
    </p>
    <div class="container__text__timing">
     <div class="container__text__timing_time">
      <h2>Hands-on Time</h2>
      <p>30 min</p>
     </div>
     <div class="container__text__timing_time">
      <h2>Total Time</h2>
      <p>40 min</p>
     </div>
     <div class="container__text__timing_time">
      <h2>Yield</h2>
      <p>40 min</p>
     </div>
    </div>
    <button class="btn">view recipe <i class="fa fa-arrow-right"></i></button>
   </div>
  </div>
                
                

            </div>

            {/* <Footer/> */}
            
        </div>
    )
}

export default SearchFlight
